class Controller {
	
	constructor(){
		this.bln = false ;
		this.stopBln = false ;
	}
	
	start = () => {
		if( this.bln ) return ;
		this.bln = true ;
		this.stopBln = false;
		this.move() ;
	}	// end of start

	move = () => {
		let { props } = this ;
		props.ctx.clearRect( 0, 0, props.w, props.h ) ;
		let i = 0, len = props.len ;
		for( ; i < len ; i += 1 ) {
			let snow = props.particles[i] ;
			snow.rotate += Math.random() * 0.045 ;
			snow.x += ( snow.size * 0.1 ) * snow.duration + snow.windVariance ;
			snow.y += snow.size * snow.duration ;
			if( snow.y > props.h ) {
				if( !this.stopBln ){
					snow.x = rdm( -Math.abs(snow.wind) , snow.w + Math.abs( snow.wind) ) ;
					let map = new Map ;
					map.set( 'fallingDown', snow.idx ) ;
					snow.update( map ) ;
				}
			}
			snow.draw() ;
		}
		if( this.bln ) {
			requestAnimationFrame( this.move ) ;
		}
	}	// end of move

	stop = () => {
		this.stopBln = true;
	}	// end of stop

	clear = () => {
		let { props } = this ;
		this.bln = false ;
		this.stopBln = false ;

		setTimeout(() => {
			props.ctx.clearRect( 0, 0, props.w , props.h ) ;
		}, 10) ;
	}	// end of clear

	change = ( num, name ) => {
		let { props } = this
		,	type = name
		,	i = 0
		, 	len = props.len ;

		for( ; i < len ; i += 1 ) {
			let map = new Map ;
			map.set( type , +num ) ;
			props.particles[i].update( map ) ;
		}
	}	// end of update

}	// end of Controller

class WinterSnow extends Controller {
	constructor(){
		super() ;
		let ctx = document.createElement('canvas').getContext('2d') ;
		let { canvas } = ctx ;

		this.props = {
			type : null ,
			ctx : ctx ,
			len : 100 ,
			speed : 1 ,
			w : canvas.width = window.innerWidth ,
			h : canvas.height = window.innerHeight ,
			windVariance : null ,
			wind : null ,
			particles : [] ,
		}

		window.addEventListener( 'resize', this.resize ) ;

	}	// end of constructor

	init = ( args ) => {

		let { type, target , len, speed, wind } = args ;
		let { props } = this ;

		target.appendChild( props.ctx.canvas ) ;

		props.type = type || 'snow' ;
		props.len = len || 100 ;		
		props.speed = speed || 1 ;
		props.windVariance = wind === undefined ? 0 : wind ;
		props.wind = props.ctx.canvas.height * props.windVariance / 2 ,

		document.querySelector('.speed').value = props.speed;
		document.querySelector('.variance').value = props.windVariance;

		this.make() ;
	}	// end of init

	resize = () => {
		let { props } = this ;
		let { canvas } = props.ctx ;
		props.w = canvas.width = window.innerWidth ;
		props.h = canvas.height = window.innerHeight ;
	}	// end of resize

	make = ( opt ) => {
		let { props } = this;
		let i = 0, len = props.len ;
		console.log( props.type ) ;
		if( props.type == 'rain' ) {
			for( ; i < len ; i += 1 ) {
				props.particles[i] = new ParticleRain( i , props ) ;			
			}	
		} else {
			for( ; i < len ; i += 1 ) {
				props.particles[i] = new Particle( i , props ) ;			
			}
		}
		console.log( props.particles ) ;
	}	// end of make

}	// end of WinterSnow

window.addEventListener('load', () => {

	let winterSnow = new WinterSnow ;
	winterSnow.init( { 
		// type : 'rain' ,
		target : document.body, 
		len : 100 ,
		speed : 1 ,
	} ) ;

	document.querySelector( '.startBtn' ).addEventListener( 'click' , () => {
		winterSnow.start() ;
	}) ;

	document.querySelector( '.variance' ).addEventListener( 'input' , ( e ) => {
		winterSnow.change( e.target.value , 'wind' ) ;
	}) ;

	document.querySelector( '.speed' ).addEventListener( 'input' , ( e ) => {
		winterSnow.change( e.target.value , 'speed' ) ;
	}) ;

	document.querySelector( '.stopBtn' ).addEventListener( 'click' , () => {
		winterSnow.stop() ;
	}) ;

	document.querySelector( '.clearBtn' ).addEventListener( 'click' , () => {
		winterSnow.clear() ;
	}) ;

}) ;

